import React, { Fragment } from 'react';
import Collapse from 'react-bootstrap/Collapse';
import Axios from 'axios';

class Card extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            data: {},
            open: false
        };
        this.handleToggle = this.handleToggle.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
    }

    static getDerivedStateFromProps(nextProps, prevState) {
        return ({
          data: nextProps.obj
        });
      }

    handleToggle() {
        this.setState({
          open: !this.state.open
        });
      }

    deleteItem() {
        console.log('Delete', this.state.data);
        Axios.delete("/announcement/delete", {id: this.state.data._id})
            .then(res => {
                console.log(res.data.message);
                this.notifyA('Success');
                Axios.get("/announcement/get")
                    .then(res => {
                        console.log(res.data.message, res.data.data);
                        this.setState({
                            data: res.data.data
                        });
                    },
                    err => {
                        console.log('Error');
                        this.notifyB('Error');
                    })
            },
            err => {
                console.log('Error');
                this.notifyB('Error');
            })
    }

    render() {
        return(
            <Fragment>
                <ul className="list-group">
                    <li className="btn btn-dark m-2" onClick={this.handleToggle}
                    aria-controls={"example-collapse-text" + this.state.data._id}
                    aria-expanded={this.state.open}
                    >
                        <div className="crossdel">
                            <i className="fa fa-window-close" aria-hidden="true" onClick={this.deleteItem}></i>
                        </div>
                        <label className="datename">Date: {this.state.data.date}</label><br/>
                        <label className="datename">Class: {this.state.data.class}</label><br/>
                    </li>
                    <Collapse in={this.state.open}>
                        <div id={"example-collapse-text" + this.state.data._id} className="homework-container card m-1 p-1" disabled>
                            <div className="card-body">
                                <label className="card-text">{this.state.data.details}</label><br/>
                                <label className="card-text">{this.state.data.author}</label>
                            </div>
                        </div>
                    </Collapse>
                </ul>
            </Fragment>
        );
    }
}

export default Card;
