import React, { Fragment } from 'react';
import Collapse from 'react-bootstrap/Collapse';
import Axios from 'axios';
import './ModalComponent.css';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

class Card extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            data: {},
            open: false
        };
        this.handleToggle = this.handleToggle.bind(this);
        this.deleteItem = this.deleteItem.bind(this);
        this.editItem = this.editItem.bind(this);
        this.editEvent = this.editEvent.bind(this);
    }

    static getDerivedStateFromProps(nextProps, prevState) {
        return ({
          data: nextProps.obj
        });
      }

    handleToggle() {
        this.setState({
          open: !this.state.open
        });
      }

    handleClose = () => {
        const modal = document.getElementById("syllabuseditmodal" + this.state.data._id);
        modal.style.display = "none";
    }
    handleShow = () => {
        const modal = document.getElementById("syllabuseditmodal" + this.state.data._id);
        modal.style.display = "block";
    }

    deleteItem() {
        this.props.delete(this.state.data._id);
    }

    editItem() {
        document.getElementById('syllabuseditbutton' + this.state.data._id).click();
        document.getElementById('syllabuseditclass' + this.state.data._id).value = this.state.data.class;
        document.getElementById('syllabuseditsubject' + this.state.data._id).value = this.state.data.subject;
        document.getElementById('syllabuseditdate' + this.state.data._id).value = this.state.data.date;
        if(this.state.data.img) {
            document.getElementById('syllabusdispedit' + this.state.data._id).hidden = false;
        } else {
            document.getElementById('syllabusdispedit' + this.state.data._id).hidden = true;
        }
        if(this.state.data.details) {
            document.getElementById('syllabuseditdetails' + this.state.data._id).defaultValue = this.state.data.details;
        } else {
            document.getElementById('syllabuseditdetails' + this.state.data._id).defaultValue = '';
        }
    }

    render() {
        return(
            <Fragment>
                <ul className="list-group">
                    <li className="btn btn-dark m-2" onClick={this.handleToggle}
                    aria-controls={"example-collapse-text" + this.state.data._id}
                    aria-expanded={this.state.open}
                    >
                        <div className="crossdel">
                            <i className="fa fa-edit m-1" aria-hidden="true" onClick={this.editItem}></i>
                            <i className="fa fa-window-close m-1" aria-hidden="true" onClick={this.deleteItem}></i>
                        </div>
                        <label className="datename">Date: {this.state.data.date}</label><br/>
                        <label className="datename">Class: {this.state.data.class}</label><br/>
                        <label className="datename">Name: {this.state.data.name}</label><br/>
                    </li>
                    <Collapse in={this.state.open}>
                        <div id={"example-collapse-text" + this.state.data._id} className="homework-container card m-1 p-1" disabled>
                            <div className="card-body">
                                <label className="card-text">{this.state.data.details}</label><br/>
                                <label className="card-text">{this.state.data.time}</label>
                            </div>
                        </div>
                    </Collapse>
                </ul>
            </Fragment>
        );
    }
}

export default Card;
